import axios from 'axios';

const API_BASE = 'http://localhost:8000/api';

export const getApprovals = () => axios.get(`${API_BASE}/approvals/`, { withCredentials: true });
export const createApproval = (data) => axios.post(`${API_BASE}/approvals/`, data, { withCredentials: true });
export const getApproval = (id) => axios.get(`${API_BASE}/approvals/${id}/`, { withCredentials: true });
export const updateApproval = (id, data) => axios.put(`${API_BASE}/approvals/${id}/`, data, { withCredentials: true });
export const deleteApproval = (id) => axios.delete(`${API_BASE}/approvals/${id}/`, { withCredentials: true });
